import React from 'react';
import ReactDOM from 'react-dom';
// import './index.css';
import App from './App';

let currDate = new Date(2022,3,27,20)
currDate = currDate.getHours()
// currDate = currDate.getMinutes()
let greeting ="";

const  cssStyle={};

if(currDate>=1 && currDate<12){
  greeting="Good Morning "
  cssStyle.color="blue";
}
else if(currDate>=12 && currDate<16){
  greeting="Good Afterrnoon"
  cssStyle.color="blue";
}
else if(currDate>=16 && currDate<20){
  greeting ="Good eveing"
  cssStyle.color="skyblue";
}
else{
  greeting = "Good Night"
  cssStyle.color="black";
}

ReactDOM.render(<h1 style={ cssStyle}> Hello sir, {greeting} </h1>,document.getElementById('root'));
